import React, { useEffect, useState } from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import Icon from "@material-ui/core/Icon";
// @material-ui/icons
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";
import styles from "assets/jss/material-kit-react/views/landingPageSections/teamStyle.js";
import $ from "jquery";
import team1 from "assets/img/faces/avatar.jpg";
import team2 from "assets/img/faces/christian.jpg";
import { getpools,getMega } from "Services/Pool";
import team3 from "assets/img/faces/kendall.jpg";
import { Grid } from "@material-ui/core";
import { useHistory } from "react-router-dom";
import { array } from "prop-types";
import _ from "lodash";
import { LocationDisabledSharp } from "@material-ui/icons";
const useStyles = makeStyles(styles);

export default function TeamSection() {
  const [poolsdata, setpools] = useState("");
  const classes = useStyles();
  const history = useHistory();
  const imageClasses = classNames(
    classes.imgRaised,
    classes.imgRoundedCircle,
    classes.imgFluid
  );

  useEffect( () => {
async function getdata(){
    const megaResult = await getMega();

    console.log('megadata',megaResult.data)


    const poolsResult = await getpools();
    console.log('Pools Result', poolsResult.data)
    // await setpools(poolsResult.data);


    // var states = poolsResult[0].data.s[0].i

    // for (const property in states) {
    //   console.log(`${states[property]}`);
    // }
    let res =megaResult && megaResult.data && _.map(megaResult.data.s,(state, index) => {
      return { label: state.i.sn, abbr: state.i.s, key: index};

  });
  console.log('states data', res)
}
getdata();
},[]);
  // const powerRoute = () => {
  //   let path1 = "/powerball";
  //   history.push(path1);
  // };

  // const megaRoute = () => {
  //   let path2 = "megamillions";
  //   history.push(path2);
  // };

  return (
    <div className={classes.section}>
      <div>
        <GridContainer>
          <GridItem xs={10} sm={6}>
            <Card
              style={{
                background:
                  "linear-gradient(80deg, rgba(2,0,36,1) 0%, rgba(214,28,102,1) 0%, rgba(59,26,136,1) 50%, rgba(228,16,92,1) 97%)",
              }}
            >
              <CardBody>
                <h2>POWERBALL</h2>
                <h3>Winning Numbers for today:</h3>
                <h4>Current EST Jackpot: </h4>
                <div style={{
                  display: 'flex',
                  flexDirection: 'row',
                  justifyContent: 'center',
                }}>
                  <div class="numbers-ball" style={{
                    backgroundColor: '#fff',
                    width: '35px',
                    height: '35px',
                    borderRadius: '20px',
                    textAlign: 'center',
                    alignItems: 'center',
                    display: 'flex',
                    margin: '10px 8px 10px 0',
                    justifyContent: 'center',
                    fontSize: '19px',
                    fontFamily: 'ProximaNova-Bold',
                    color: '#2f3e4a',
                  }
                  }>08</div>
                </div>
                <div>
                  <GridContainer>
                    <GridItem style={{ color: "white" }} xs={6}>
                      Cash Value : $157,900,000
                    </GridItem>
                    <GridItem style={{ color: "white" }} xs={6}>
                      Next Draw : Tomorrow
                    </GridItem>
                  </GridContainer>
                </div>
                <Button
                  href="#top"
                  // onClick={powerRoute}
                  style={{ background: "black" }}
                  startIcon={<Icon>send</Icon>}
                >
                  Previous Winning Numbers
                </Button>
              </CardBody>
            </Card>
          </GridItem>
          <GridItem xs={12} sm={6}>
            <Card
              style={{
                background:
                  "linear-gradient(to right bottom, #430089, #82ffa1)",
              }}
            >
              <CardBody>
                <h2>MEGAMILLIONS</h2>
                <h3>Winning Numbers for today:</h3>

                <div style={{
                  display: 'flex',
                  flexDirection: 'row',
                  justifyContent: 'center',
                }}>
                  {/* {poolsdata && poolsdata.data.map((data, index) => (
                    <div class="numbers-ball" style={{
                      backgroundColor: '#fff',
                      width: '35px',
                      height: '35px',
                      borderRadius: '20px',
                      textAlign: 'center',
                      alignItems: 'center',
                      display: 'flex',
                      margin: '10px 8px 10px 0',
                      justifyContent: 'center',
                      fontSize: '19px',
                      fontFamily: 'ProximaNova-Bold',
                      color: '#2f3e4a',
                    }
                    }>some number</div>
                  ))} */}
                </div>
                {/* <img src={require("assets/img/megamillions.JPG")}></img> */}
                <h4>Current EST Jackpot: </h4>
                <h5 style={{ color: "white", background: "black" }}>
                  $200,000,000
                </h5>
                <div>
                  <GridContainer>
                    <GridItem style={{ color: "white" }} xs={6}>
                      Cash Value : $155,700,000
                    </GridItem>
                    <GridItem style={{ color: "white" }} xs={6}>
                      Next Draw : Tomorrow
                    </GridItem>
                  </GridContainer>
                </div>
                <Button
                  href="#top"
                  // onClick={megaRoute}
                  style={{ background: "black" }}
                  startIcon={<Icon>send</Icon>}
                >
                  Previous Winning Numbers
                </Button>
              </CardBody>
            </Card>
          </GridItem>
        </GridContainer>
      </div>
    </div>
  );
}
