import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";

import MenuItem from '@material-ui/core/MenuItem';
import MoreVertIcon from '@material-ui/icons/MoreVert';
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core components
import { Grid } from "@material-ui/core";
import GridItem from "components/Grid/GridItem.js";
import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";
import Hidden from "@material-ui/core/Hidden";
import Drawer from "@material-ui/core/Drawer";
import Box from '@material-ui/core/Box';
// @material-ui/icons
import Menu from "@material-ui/icons/Menu";
import AssignmentIndIcon from '@material-ui/icons/AssignmentInd';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
// core components
import styles from "assets/jss/material-kit-react/components/headerStyle.js";
import { useHistory } from "react-router-dom";
const useStyles = makeStyles(styles);

export default function Header(props) {

  const ITEM_HEIGHT = 48;

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const history = useHistory();

  const options = [
    'HOME',
    'POWERBALL',
    'MEGAMILLIONS',
    'STATE NUMBERS',
    'LOG IN',
    'SIGN UP',
  ];

  const homeRoute=() =>{
    let homePath="/landing-page";
    history.push(homePath);

  }
  const signUpRoute = () => {
    let signPath= "/signup";
    history.push(signPath);

  }

  const logInRoute = () => {
    let loginPath= "/login";
    history.push(loginPath);

  }
  const powerRoute = () => {
    let powerPath= "/powerball";
    history.push(powerPath);

  }

  const stateRoute=() =>{
    let statePath="/states";
    history.push(statePath);

  }

  const megaRoute = () => {
    let megaPath= "megamillions";
    history.push(megaPath);

  }
  const classes = useStyles();
  const [mobileOpen, setMobileOpen] = React.useState(false);
  React.useEffect(() => {
    if (props.changeColorOnScroll) {
      window.addEventListener("scroll", headerColorChange);
    }
    return function cleanup() {
      if (props.changeColorOnScroll) {
        window.removeEventListener("scroll", headerColorChange);
      }
    };
  });
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  const headerColorChange = () => {
    const { color, changeColorOnScroll } = props;
    const windowsScrollTop = window.pageYOffset;
    if (windowsScrollTop > changeColorOnScroll.height) {
      document.body
        .getElementsByTagName("header")[0]
        .classList.remove(classes[color]);
      document.body
        .getElementsByTagName("header")[0]
        .classList.add(classes[changeColorOnScroll.color]);
    } else {
      document.body
        .getElementsByTagName("header")[0]
        .classList.add(classes[color]);
      document.body
        .getElementsByTagName("header")[0]
        .classList.remove(classes[changeColorOnScroll.color]);
    }
  };
  const { color, rightLinks, leftLinks, brand, fixed, absolute } = props;
  const appBarClasses = classNames({
    [classes.appBar]: true,
    [classes[color]]: "blue",
    [classes.absolute]: absolute,
    [classes.fixed]: fixed
  });
  const brandComponent = <Button className={classes.title}>{brand}</Button>;
  return (
//     <Grid lg={4} xs={12} md={6}> 
//     <AppBar className={appBarClasses}>

//       <Toolbar className={classes.container}>
//         <GridItem lg={4} xs={12} md={6}>
//         <Box display='flex' flexGrow={4}>
//           <Button href="#top" onClick={homeRoute} style={{backgroundColor : "#f4a261"}}>Home</Button>
//           <div>-</div>
//           <Button href="#top" onClick={powerRoute} style={{backgroundColor : "#f4a261"}}>Powerball</Button>
//           <div>-</div>
//           <Button href="#top" onClick={megaRoute} style={{backgroundColor : "#f4a261"}}>Megamillions</Button>
//           <div>-</div>
//           <Button href="#top" onClick={stateRoute} style={{backgroundColor : "#f4a261"}}>State numbers</Button>
//         </Box>
//         </GridItem>
//           <Button href="#top" onClick={logInRoute} endIcon={<AccountCircleIcon/>} style={{backgroundColor : "#f4a261"}}>Log In</Button>
//           <div>-</div>
//           <Button href="#top" onClick={signUpRoute} endIcon={<AssignmentIndIcon/>} style={{backgroundColor : "#f4a261"}}>Sign Up</Button>
//           <div>-</div>

//         {leftLinks !== undefined ? brandComponent : null}
//         <div className={classes.flex}>
//           {leftLinks !== undefined ? (
//             <Hidden smDown implementation="css">
//               {leftLinks}
//             </Hidden>
//           ) : (
//               brandComponent
//             )}
//         </div>
//         <Hidden smDown implementation="css">
//           {rightLinks}
//         </Hidden>
//         <Hidden mdUp>
//           <IconButton
//             background="blue"
//             aria-label="open drawer"
//             onClick={handleDrawerToggle}
//           >
//             <Menu />
//           </IconButton>
//         </Hidden>
//       </Toolbar>
//       {/* <Hidden mdUp implementation="js"> */}
//         <Drawer
//           variant="temporary"
//           anchor={"right"}
//           open={mobileOpen}
//           classes={{
//             paper: classes.drawerPaper
//           }}
//           onClose={handleDrawerToggle}
//         >
//           <div className={classes.appResponsive}>
//             {leftLinks}
//             {rightLinks}
//           </div>
//         </Drawer>
//       {/* </Hidden> */}
//     </AppBar>
//     </Grid>
//   );
// }

// Header.defaultProp = {
//   color: "white"
// };

// Header.propTypes = {
//   color: PropTypes.oneOf([
//     "primary",
//     "info",
//     "success",
//     "warning",
//     "danger",
//     "transparent",
//     "white",
//     "rose",
//     "dark"
//   ]),
//   rightLinks: PropTypes.node,
//   leftLinks: PropTypes.node,
//   brand: PropTypes.string,
//   fixed: PropTypes.bool,
//   absolute: PropTypes.bool,
//   // this will cause the sidebar to change the color from
//   // props.color (see above) to changeColorOnScroll.color
//   // when the window.pageYOffset is heigher or equal to
//   // changeColorOnScroll.height and then when it is smaller than
//   // changeColorOnScroll.height change it back to
//   // props.color (see above)
//   changeColorOnScroll: PropTypes.shape({
//     height: PropTypes.number.isRequired,
//     color: PropTypes.oneOf([
//       "primary",
//       "info",
//       "success",
//       "warning",
//       "danger",
//       "transparent",
//       "white",
//       "rose",
//       "dark"
//     ]).isRequired
//   })
// };

<div>
      <IconButton
        aria-label="more"
        aria-controls="long-menu"
        aria-haspopup="true"
        onClick={handleClick}
      >
        <MoreVertIcon />
      </IconButton>
      <Menu
        id="long-menu"
        anchorEl={anchorEl}
        keepMounted
        open={open}
        onClose={handleClose}
        PaperProps={{
          style: {
            maxHeight: ITEM_HEIGHT * 4.5,
            width: '20ch',
          },
        }}
      >
        {options.map((option) => (
          <MenuItem key={option} selected={option === 'Pyxis'} onClick={handleClose}>
            {option}
          </MenuItem>
        ))}
      </Menu>
    </div>
  );
}
